LOCK TABLE auth_group IN EXCLUSIVE MODE;
-- Update the sequence
SELECT setval(
        'auth_group_id_seq',
        COALESCE(
            (
                SELECT MAX(id) + 1
                FROM auth_group
            ),
            1
        ),
        false
    );
COMMIT;
